import React, { Component } from 'react';
import '../css/login-page.css';
import LoginForm from './login-form';
import RegisterForm from './register-form';
import ForgetPass from './forget-pass';
import axios from 'axios';

class Login extends Component {
	constructor(props){
		super(props);
		this.state={
			active:1
		}
	}
    componentDidMount(){
		console.log('start');
        //this.testAPI();
		axios.get('http://192.168.9.253:8181/api/v1/hello')
		
		.then(function (response) {
			console.log(response.data.funct)
		})
		
		.catch(function (error) {
			
			console.log(error);
		})

		axios.post('/user', {
			firstName: 'Fred',
			lastName: 'Flintstone'
		  })
    }
    testAPI = () => {
		let headers = new Headers();

		headers.append('Content-Type', 'application/json');
		headers.append('Accept', 'application/json');
	  
		headers.append('Access-Control-Allow-Origin', 'http://127.0.0.1:3000');
		headers.append('Access-Control-Allow-Credentials', 'true');
		headers.append("Access-Control-Allow-Headers", "Origin, X-Requested-With,");
		headers.append('GET', 'POST', 'OPTIONS');
        fetch('https://cors-anywhere.herokuapp.com/http://192.168.9.253:8181/api/v1/hello',{headers:headers,credentials: 'include',
		method: 'GET',})
        .then(rsp => rsp.json())
		.then(data =>console.log(data))
		.catch(() => console.log("Can’t access response. Blocked by browser?"))
            
        
    }
	clickLogin = () => {
		this.setState({active:1});
		document.getElementById("login").style.borderBottom= '#3399FF solid 2px';
		document.getElementById("register").style.borderBottom= '#DDDDDD solid 2px';
	}
	clickRegister = () => {
		this.setState({active :2})
		document.getElementById("register").style.borderBottom= '#3399FF solid 2px';
		document.getElementById("login").style.borderBottom= '#DDDDDD solid 2px';
	}

	showForgetPassForm = () => {
		this.setState({active:3})
	}
    render() {
		let activeComponent; 
		if (this.state.active===1) {
			activeComponent=<LoginForm  onClickForgetPass={this.showForgetPassForm}/>;		
		}
		else if (this.state.active===2) {
			activeComponent =<RegisterForm onClickForgetPass={this.showForgetPassForm}/>;	
		}
		else {
			activeComponent=<ForgetPass/>;
		}
        return (
			<React.Fragment>
				<div className='row'>
						<label id='register' onClick={this.clickRegister}>Đăng ký</label>
						<label id='login' onClick={this.clickLogin} >Đăng nhập</label>
				</div>
				<div className='form'>
					{activeComponent}
					
					{/*div className='more'>
						<button><i class="fab fa-facebook"></i></button>
		</div>*/}
				</div>
				
			</React.Fragment>
          );
    }
}
 
export default Login;
