import React, { Component } from 'react';

class LoginForm extends Component {
    state={ 
        message:null
    }
    async componentDidMount(){
        const response = await fetch('https://192.168.9.253:8181/api/v1/welcome');
        this.email = await response.json();
       
    }
    testAPI = () => {
        fetch('http://192.168.9.253:8181/api/v1/hello')
        .then(rsp => rsp.json())
        .then(message =>{
            this.message=message;
            alert(message);
        })
    }
    fetchHouses = () => {
        fetch("/houses.json")
        .then(rsp => rsp.json())
        .then(allHouses =>{
          this.allHouses = allHouses;
          this.determineFeaturedHouse();
          this.determineUniqueCountries();
        })
      }
    clickForgetPass = () => {
        this.props.onClickForgetPass();
    }
    changeEmail = (e) => {
        this.setState({email: e.target.value})
    }
    changePass = (e) => {
        this.setState({pass: e.target.value})
    }
    handleSubmit = () => {
        alert('123');
    }
    render() { 
        return ( 
            <React.Fragment>
                <label>
                    Đăng nhập
                </label>
                <form>
                    <input type='email' placeholder='Email' autoComplete='on' onChange={this.changeEmail}/>
                    <input type='password' placeholder='Mật khẩu'  autoComplete='on' onChange={this.changePass}/>
                    <button className='submit' type='submit' onSubmit={this.handleSubmit}>Đăng nhập</button>
                    <button onClick={this.clickForgetPass} className='forget-pass'>Quên mật khẩu?</button>
                    <button onClick={this.testAPI}>Test</button>
                </form>
            </React.Fragment>
         );
    }
}
 
export default LoginForm;
