import React, { Component } from 'react';
import img3 from '../images/img3.jpg';
import img4 from '../images/img4.jpg';
import Popup from 'reactjs-popup'

const imgBackground = [img3,img4] ;
class Image extends Component {
    state = {  
        image : imgBackground[1]
    }

    changeImage = () =>{
        //document.getElementById('image-background').s
        let currentImage=this.state.image;
        if (currentImage ===imgBackground[0]) this.setState({image: imgBackground[1]});
        if (currentImage ===imgBackground[1]) this.setState({image: imgBackground[0]});
    }
    componentDidMount(){
        this.imageEffect=setInterval(this.changeImage, 3000);
    }
    componentWillUnmount(){
        clearInterval(this.imageEffect);
    }
    render() { 
        return ( 
            <React.Fragment>
                <h1>Chỉnh sửa ảnh sản phẩm trên Shopee</h1>
                <h2>Mọi thắc mắc xin liên hệ với chúng tôi <span id='hint'>tại đây</span></h2>
            </React.Fragment>
         );
    }
}
 
export default Image;
