import React, { Component } from 'react';
import axios from 'axios';

class LoginForm extends Component {
    state={ }
    
    clickForgetPass = () => {
        this.props.onClickForgetPass();
    }
    changeEmail = (e) => {
        this.setState({email: e.target.value})
    }
    changePass = (e) => {
        this.setState({pass: e.target.value})
    }
    handleSubmit = (e) => {
        e.preventDefault();
        axios.post('http://192.168.9.253:8181/api/v1/login',{
            email: this.state.email,
			password: this.state.pass
		})
        .then( (respone)=>{
            if(respone.data.success===1){
                alert('Đăng nhập thành công');
            }else alert('Email hoặc mật khẩu không đúng');
        })
        .catch((error) => alert('Đăng nhập thất bại! Hãy thử lại sau'))
        
    }
    render() { 
        return ( 
            <React.Fragment>
                <label>
                    Thông tin đăng nhập
                </label>
                <form onSubmit={this.handleSubmit}> 
                    <input type='email' placeholder='Email' required autoComplete='o' onChange={this.changeEmail}/>
                    <input type='password' placeholder='Mật khẩu' required autoComplete='on' onChange={this.changePass}/>
                    <button className='submit' type='submit' >Đăng nhập</button>
                    <button onClick={this.clickForgetPass} className='forget-pass'>Quên mật khẩu?</button>
                </form>
            </React.Fragment>
         );
    }
}
 
export default LoginForm;
