import React, { Component } from 'react';
import '../css/login-page.css';
import LoginForm from './login-form';
import RegisterForm from './register-form';
import ForgetPass from './forget-pass';

class Login extends Component {
	constructor(props){
		super(props);
		this.state={
			active:3
		}
		this.showForgetPassForm=this.showForgetPassForm.bind(this);
	}
    
	clickLogin = () => {
		this.setState({active:1});
		document.getElementById("login").style.borderBottom= '#3399FF solid 2px';
		document.getElementById("register").style.borderBottom= '#DDDDDD solid 2px';
	}
	clickRegister = () => {
		this.setState({active :2})
		document.getElementById("register").style.borderBottom= '#3399FF solid 2px';
		document.getElementById("login").style.borderBottom= '#DDDDDD solid 2px';
	}

	showForgetPassForm = () => {
		this.setState({active:3})
	}
    render() {
		let activeComponent; 
		let forgetPass;
		if (this.state.active===1) {
			activeComponent=<LoginForm/>;
			forgetPass=<button onClick={this.clickForgetPass} className='forget-pass'>Quên mật khẩu?</button>;
		}
		else if (this.state.active===2) {
			activeComponent =<RegisterForm/>;
			forgetPass=<button onClick={this.clickForgetPass} className='forget-pass'>Quên mật khẩu?</button>;
		}
		else activeComponent=<ForgetPass/>;
        return (
			<div className='a'>
				<div className='row'>
						<label id='register' onClick={this.clickRegister}>Đăng ký</label>
						<label id='login' onClick={this.clickLogin} >Đăng nhập</label>
				</div>
				<div className='form'>
					{activeComponent}
					<button onClick={this.clickForgetPass} className='forget-pass'>Quên mật khẩu?</button>
				</div>
				
			</div>
          );
    }
}
 
export default Login;
