import React, { Component } from 'react';
import '../css/login-page.css';
import LoginForm from './login-form';
import RegisterForm from './register-form';

class Login extends Component {
	constructor(props){
		super(props);
		this.state={
			active:1
		}
		this.handleClick=this.handleClick.bind(this);
	}
    state = {
		active:1
	  }
	
	cl = () => {
		this.setState((prevState) => ({active: (prevState+1) % 2}) )
	}
    render() {
		let activeComponent;
		if (this.state.active===1) activeComponent=<LoginForm/>;
		else activeComponent =<RegisterForm/>
        return (
			<div className='a'>
				<div className='row'>
						<label onClick={this.clickRegister}>Đăng ký</label>
						<label onClick={this.clickLogin}>Đăng nhập</label>
				</div>
				<div className='form'>
					{activeComponent}
				</div>
			</div>
          );
    }
}
 
export default Login;
