import React, { Component } from 'react';
import img3 from '../images/img3.jpg';
import img4 from '../images/img4.jpg';

const imgBackground = [img3,img4] ;
class Image extends Component {
    state = {  
        image : imgBackground[1]
    }

    changeImage = () =>{
        if (this.state.image ===imgBackground[0]) this.setState({image: imgBackground[1]});
        if (this.state.image ===imgBackground[1]) this.setState({image: imgBackground[0]});
    }
    componentDidMount(){
        setInterval(this.changeImage.bind(this), 1000);
    }
    render() { 
        return ( 
            <React.Fragment>
                <img src={this.state.image} alt='img1'/>
            </React.Fragment>
         );
    }
}
 
export default Image;
