import React, { Component } from 'react';
import '../css/login-page.css';
import LoginForm from './login-form';
import RegisterForm from './register-form';

class Login extends Component {
    state = {
		active:1
	  }
	
	handleClick = () => {
		this.setState((prevState) => ({active: (prevState+1) % 2}) )
	}
    render() {
		let activeComponent;
		if (this.state.active) 
        return (
			<div className='a'>
				<div className='row'>
						<label onClick={this.handleClick}>Đăng ký</label>
						<label onClick={this.handleClick}>Đăng nhập</label>
				</div>
				<div className='form'>
					<RegisterForm/>
				</div>
			</div>
          );
    }
}
 
export default Login;
