import React, { Component } from 'react';
import '../css/login-page.css';
import LoginForm from './login-form';
import RegisterForm from './register-form';

class Login extends Component {
	constructor(props){
		super(props);
		this.state={
			active:1
		}
	}
    
	clickLogin = () => {
		this.setState({active:1})
	}
	clickRegister = () => {
		this.setState({active :2})
	}
    render() {
		let activeComponent;
		if (this.state.active===1) activeComponent=<LoginForm active={this.state.active}/>;
		else activeComponent =<RegisterForm active={this.state.active}/>
        return (
			<div className='a'>
				<div className='row'>
						<label onClick={this.clickRegister}>Đăng ký</label>
						<label onClick={this.clickLogin}>Đăng nhập</label>
				</div>
				<div className='form'>
					{activeComponent}
				</div>
			</div>
          );
    }
}
 
export default Login;
