import React, { Component } from 'react';

class RegisterForm extends Component {
    state = {  }
    clickForgetPass = () => {
        this.props.onClickForgetPass();
    }
    channgeName = (e) => {
        this.setState({name: e.target.value})
    }
    changePhone = (e) => {
        this.setState({phone: e.target.value})
    }
    changeEmail = (e) => {
        this.setState({email: e.target.value})
    }
    changePass = (e) => {
        this.setState({pass: e.target.value})
    }
    changeConfirmPass = (e) => {
        this.setState({confirmPass: e.target.value})
    }
    changeLocation = (e) => {
        this.setState({location: e.target.value})
    }
    handleSubmit = () => {
        alert
    }
    render() { 
        return ( 
            <React.Fragment>
                <label>
                    Đăng ký
                </label>
                <div className='register-form'>
                    <label>
                        Luôn miễn phí
                    </label>
                    <form><div>
                        <input type='text' placeholder='Họ và tên' onChange={this.channgeName} autoComplete='on'/>
                        <input type='number' placeholder='Số điện thoại' autoComplete='on' onChange={this.changePhone}/>
                        <input type='email' placeholder='Email' autoComplete='on' onChange={this.changeEmail}/>
                        <input type='password' placeholder='Mật khẩu' autoComplete='on' onChange={this.changePass}/>
                        <input type='password' placeholder='Nhập lại mật khẩu' autoComplete='off' onChange={this.changeConfirmPass}/>
                        <label htmlFor='location'>Sàn đăng ký tại</label>
                        <select id='location' onChange={this.changeLocation}>
                            <option value='' hidden>Chọn</option>
                            <option value='1'>Việt Nam</option>
                            <option value='2'>Indonesia</option>
                            <option value='3'>Malaysia</option>
                            <option value='4'>Philippines</option>
                            <option value='5'>Singapore</option>
                            <option value='6'>Thái Lan</option>
                        </select>
                        </div>
                        <button className='submit' onSubmit={this.handleSubmit}>Đăng ký</button>
                        <button onClick={this.clickForgetPass} className='forget-pass'>Quên mật khẩu?</button>
                    </form>
                </div>
                </React.Fragment>
         );
    }
}
 
export default RegisterForm;
